import React, { useState } from 'react';
import Select from 'react-select';

function App() {
  const cities = [
    { value: { latitude: 51.5074, longitude: -0.1278 }, label: 'London' },
    { value: { latitude: 40.7128, longitude: -74.006 }, label: 'New York' },
    { value: { latitude: 35.6895, longitude: 139.6917 }, label: 'Tokyo' },
    { value: { latitude: 48.8566, longitude: 2.3522 }, label: 'Paris' }
  ];

  const [selectedCity, setSelectedCity] = useState(null);
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const getWeather = async () => {
    if (!selectedCity) return;
    setLoading(true);
    setError(null);
    setWeather(null);
    const { latitude, longitude } = selectedCity.value;
    try {
      const res = await fetch(`https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&current_weather=true`);
      const data = await res.json();
      setWeather(data.current_weather);
    } catch (err) {
      setError('Failed to fetch weather data.');
    }
    setLoading(false);
  };

  return (
    <div className="flex flex-col space-y-3 max-w-md mx-auto mt-10 p-4 border rounded shadow">
      <h2 className="text-xl font-bold mb-2">Weather App</h2>
      <Select
        options={cities}
        value={selectedCity}
        onChange={setSelectedCity}
        placeholder="Select a city..."
      />
      <button
        className="bg-green-400 w-full py-3 text-white text-sm font-bold hover:scale-105 transition-all duration-200 ease-in-out"
        onClick={getWeather}
        disabled={!selectedCity || loading}
      >
        {loading ? 'Loading...' : 'Get Weather'}
      </button>
      {error && <div className="text-red-500">{error}</div>}
      {weather && (
        <div className="mt-4 p-3 bg-blue-100 rounded">
          <div>Temperature: {weather.temperature}Â°C</div>
          <div>Wind Speed: {weather.windspeed} km/h</div>
          <div>Weather Code: {weather.weathercode}</div>
        </div>
      )}
    </div>
  );
}

export default App;
